-- ----------------------------
-- Triggers structure for table nonstarbookings
-- ----------------------------
DROP TRIGGER IF EXISTS `update_sales_after_insert_non`;
delimiter ;;
CREATE TRIGGER `update_sales_after_insert_non` AFTER INSERT ON `nonstarbookings` FOR EACH ROW BEGIN
    -- 增加对应房型的营销量
    UPDATE NonStarRoomTypes
    SET sales = sales + 1,
        quantity = quantity - 1
    WHERE id = NEW.room_type_id;

    -- 更新营销表中的营销量
    UPDATE NonStarMarketingRecords
    SET total_sales = (
        SELECT SUM(sales)
        FROM NonStarRoomTypes
        WHERE hotel_id = NEW.hotel_id AND id = NEW.room_type_id
    )
    WHERE hotel_id = NEW.hotel_id AND room_type_id = NEW.room_type_id;
END
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table nonstarbookings
-- ----------------------------
DROP TRIGGER IF EXISTS `update_quantity_after_checkout_non`;
delimiter ;;
CREATE TRIGGER `update_quantity_after_checkout_non` AFTER UPDATE ON `nonstarbookings` FOR EACH ROW BEGIN
    IF NEW.check_out_time <= NOW() THEN
        -- 更新对应房型的剩余量
        UPDATE NonStarRoomTypes
        SET quantity = quantity + 1
        WHERE id = NEW.room_type_id;
    END IF;
END
;;
delimiter ;

