    DECLARE min_price INT;

    -- 获取对应酒店下的最低价格
    SELECT MIN(price) INTO min_price
    FROM StarRoomTypes
    WHERE hotel_id = NEW.hotel_id;

    -- 更新 StarHotels 表中的最低价格
    UPDATE StarHotels
    SET min_price = min_price
    WHERE id = NEW.hotel_id;
END
;;
